<?php //class.Rectangle.phpclass Rectangle {  public $height;  public $width;  public function __construct($width, $height) {    $this- > width = $width;    $this- > height = $height;  }  public function getArea() {    return $this- > height * $this- > width;  }}?><?php //class.Square.php //This code overrides both the constructor and the getArea() method require_once(‘class.Rectangle.php’);class Square extends Rectangle {  public function __construct($size) {      $this- > height = $size;      $this- > width = $size;    }  public function getArea() {      return pow($this- > height, 2);    }}?>